import React, {useState} from "react";
import ExpenseItem from "./ExpenseItem";
import "./ExpensesList.css";
import Card from "../UI/Card"
import ExpensesFilter from "./ExpensesFilter";

function ExpensesList(props) {


  const [filteredYear, setFilteredYear] = useState("2020")

  function handleFilterChange(year){
      setFilteredYear(year)   
  }

  const data = props.data.filter(expense => expense.date.getFullYear() === parseFloat(filteredYear))

  return (
    <div>
      <Card className="expenses">
      <ExpensesFilter selected = {filteredYear} onFilterChange = {handleFilterChange}/>
        {data.map(expense => {
          return <ExpenseItem key = {expense.key} title = {expense.title} amount = {expense.amount} date = {expense.date}/>
        })}
      </Card>
    </div>
  );
}

export default ExpensesList;
